app.config(function($stateProvider, $urlRouterProvider) {
    // For any unmatched url, redirect to /
    $urlRouterProvider.otherwise("/");
    // States
    $stateProvider
        .state('index', {
            url: "/",
            views: {
                '': { templateUrl: 'layout/intro.html' },
                'header@index': { templateUrl: 'layout/header.html' },
                'footer@index': { templateUrl: 'layout/footer.html' }
            }
        })
        .state('test', {
            url: "/test",
            views: {
                '': { templateUrl: 'layout/test.html'},
                'header@test': { templateUrl: 'layout/header.html' },
                'footer@test': { templateUrl: 'layout/footer.html' }
            }
        })
        .state('contact', {
            url:"/contact",
            views: {
                '': { templateUrl: 'layout/contact.html'},
                'header@contact': { templateUrl: 'layout/header.html' },
                'footer@contact': { templateUrl: 'layout/footer.html' }
            }
        });
});
